#include <stdint.h>

#define ALIAS_BASE			0x22000000U
#define BIT_BAND_MEM_ADDR	0x20000200U
#define BIT_BAND_BASE		0x20000000U
#define BIT					7U

#define ALIAS_ADDR (ALIAS_BASE + (32 * (BIT_BAND_MEM_ADDR - BIT_BAND_BASE)) + (BIT * 4));

int main(void){

	// To clear bit 7:
	// Without BitBanding:

	uint8_t *ptr = (uint8_t*) BIT_BAND_MEM_ADDR;

	*ptr = 0xFF;

	*ptr &= ~(1 << 7);

	// Using BitBanding:

	*ptr = 0xFF;

	uint8_t *aliasAddr = (uint8_t*) ALIAS_ADDR;

	*aliasAddr = 0;

    /* Loop forever */
	for(;;);
}
